package com.example.finalproject.adapters;

import android.content.Context;
import android.content.Intent;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;
import android.widget.TextView;
import androidx.annotation.NonNull;
import androidx.recyclerview.widget.RecyclerView;

import com.bumptech.glide.Glide;
import com.example.finalproject.Restaurant_Preferance_Activity;
import com.example.finalproject.items.Hotel;
import com.example.finalproject.items.Flight;
import com.example.finalproject.R;

import java.util.List;

public class HotelListAdapter extends RecyclerView.Adapter<HotelListAdapter.HotelViewHolder> {
    private List<Hotel> hotelList;
    private Context context;
    private Flight selectedFlight, selectedReturnFlight;  // extra data of the selected flights

    public HotelListAdapter(Context context, List<Hotel> hotelList, Flight selectedFlight, Flight selectedReturnFlight) {
        this.context = context;
        this.hotelList = hotelList;
        this.selectedFlight = selectedFlight;
        this.selectedReturnFlight = selectedReturnFlight;
    }

    @NonNull
    @Override
    public HotelViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
        View view = LayoutInflater.from(context).inflate(R.layout.hotel_layout, parent, false);
        return new HotelViewHolder(view);
    }

    @Override
    public void onBindViewHolder(@NonNull HotelViewHolder holder, int position) {
        Hotel hotel = hotelList.get(position);

        // Set hotel details
        holder.name.setText(hotel.getType().substring(0, 1).toUpperCase() + hotel.getType().substring(1)+": " + hotel.getName());
        holder.location.setText("Location: " + hotel.getLocation());
        holder.rating.setText("Rating: " + hotel.getRating() + " stars");
        holder.distance.setText("Distance from the Center: "+ hotel.getDistanceFromCenterKm() + " km");
        holder.price.setText(hotel.getPricePerNight() + "$");

        String imageUrl = "data:image/jpeg;base64,";
        //TODO change to the hotel img
        Glide.with(context)
                .load(imageUrl)
                .placeholder(R.drawable.hotel_default)  // Optional placeholder while loading
                .error(R.drawable.hotel_default)    // Optional error placeholder
                .into(holder.profileImg);


        // Set OnClickListener for each hotel item
        holder.itemView.setOnClickListener(v -> {
            Intent intent = new Intent(context, Restaurant_Preferance_Activity.class);
            intent.putExtra("selectedHotel", hotel);
            intent.putExtra("selectedFlight", selectedFlight);
            intent.putExtra("selectedReturnedFlight", selectedReturnFlight);
            context.startActivity(intent);

        });
    }

    @Override
    public int getItemCount() {
        return hotelList.size();
    }

    public static class HotelViewHolder extends RecyclerView.ViewHolder {

        TextView name, location, rating, distance, price;
        ImageView profileImg;

        public HotelViewHolder(@NonNull View itemView) {
            super(itemView);
            name = itemView.findViewById(R.id.hotel_name);
            location = itemView.findViewById(R.id.location);
            rating = itemView.findViewById(R.id.rating);
            distance = itemView.findViewById(R.id.distance);
            price = itemView.findViewById(R.id.price);
            profileImg = itemView.findViewById(R.id.profileImg);
        }
    }

}
